    def retranslateUi(self, connexionWindow):
        _translate = QtCore.QCoreApplication.translate
        connexionWindow.setWindowTitle(_translate("connexionWindow", "Hermes - Secure messaging"))
        self.label.setText(_translate("connexionWindow", "Password :"))
        self.label_3.setText(_translate("connexionWindow", "HERMES"))
        self.pushButton_login.setText(_translate("connexionWindow", "Login"))

    def login(self):
        """
        login to the application
        :param self: window parameters
        """
        #get user password
        self.password = str(self.lineEdit_login.text())
        #get the hash in the file
        hash = tools.ReadFile(self.password, "hash")
        #create the second hash with the current password
        hash2 = tools.toSHA(tools.salt+self.password)
        #compare the both hashs
        if (hash == hash2):
            # initialization of the window home
            homeWindow = QtWidgets.QMainWindow()
            uiHome = homeUI.Ui_home()
            uiHome.setupUi(homeWindow, self.password)
            #add home window to the stackedLayout
            self.stackedLayout.addWidget(homeWindow)
            self.stackedLayout.setCurrentIndex(1)
        else:
            self.label_event.setStyleSheet("color: red")
            self.label_event.setText("Invalid password")


