    def register(self):
        """
        register the user during the first connexion
        :param password: original text
        :return: SHA in hexa
        """
        self.password1 = str(self.lineEdit_password.text())
        self.password2 = str(self.lineEdit_confirmPassword.text())
        # check if passwords are the same
        if self.password1 == self.password2:
            # keep the password in memory
            homeUI.Ui_home.currentPassword = self.password1
            # hash the password with sha256 to hexa
            hash = tools.toSHA(tools.salt+self.password1)
            # encrypt contacts
            datasEncrypt = tools.toAES('{"contacts": []}', tools.toPasswordEncode(self.password1))
            # JSON datas
            data = {"hash": hash, "datas": datasEncrypt}
            tools.WriteFile(self.password1, data, "datas")
            # initialization of the window home
            homeWindow = QtWidgets.QMainWindow()
            uiHome = homeUI.Ui_home()
            uiHome.setupUi(homeWindow, self.password1)
            #add home window to the stackedLayout
            self.stackedLayout.addWidget(homeWindow)
            self.stackedLayout.setCurrentIndex(1)
        else:
            self.label_event.setStyleSheet("color: red")
            self.label_event.setText("Passwords aren't the same")


